        List<CartItem> collect = values.stream().map((obj) -> {
            String str = (String) obj;
            CartItem cartItem = JSON.parseObject(str, CartItem.class);
            return cartItem;
        }).collect(Collectors.toList());
        return collect;
    }
    return null;
}
