@Override
public void run() {
    try {
        // æ‰¾åˆ°æ‰€æœ‰ä¸´æ—¶å®ä¾‹çš„åˆ—è¡¨
        List<Instance> instances = service.allIPs(true);

        // first set health status of instances:
        for (Instance instance : instances) {
            // åˆ¤æ–­ å¿ƒè·³é—´éš”ï¼ˆå½“å‰æ—¶é—´ - æœ€åä¸€æ¬¡å¿ƒè·³æ—¶é—´ï¼‰ æ˜¯å¦å¤§äº å¿ƒè·³è¶…æ—¶æ—¶é—´ï¼Œé»˜è®¤15ç§’
            if (System.currentTimeMillis() - instance.getLastBeat() > instance.getInstanceHeartBeatTimeOut()) {
                if (!instance.isMarked()) {
                    if (instance.isHealthy()) {
                        // å¦‚æœè¶…æ—¶ï¼Œæ ‡è®°å®ä¾‹ä¸ºä¸å¥åº· healthy = false
                        instance.setHealthy(false);
 
                        // å‘å¸ƒå®ä¾‹çŠ¶æ€å˜æ›´çš„äº‹ä»¶
                        getPushService().serviceChanged(service);
                        ApplicationUtils.publishEvent(new InstanceHeartbeatTimeoutEvent(this, instance));
                    }
                }
            }
        }

        if (!getGlobalConfig().isExpireInstance()) {
            return;
        }

        // then remove obsolete instances:
        for (Instance instance : instances) {

            if (instance.isMarked()) {
                continue;
            }
           // åˆ¤æ–­å¿ƒè·³é—´éš”ï¼ˆå½“å‰æ—¶é—´ - æœ€åä¸€æ¬¡å¿ƒè·³æ—¶é—´ï¼‰æ˜¯å¦å¤§äº å®ä¾‹è¢«åˆ é™¤çš„æœ€é•¿è¶…æ—¶æ—¶é—´ï¼Œé»˜è®¤30ç§’
            if (System.currentTimeMillis() - instance.getLastBeat() > instance.getIpDeleteTimeout()) {
                // å¦‚æœæ˜¯è¶…è¿‡äº†30ç§’ï¼Œåˆ™åˆ é™¤å®ä¾‹
                Loggers.SRV_LOG.info("[AUTO-DELETE-IP] service: {}, ip: {}", service.getName(),
                                     JacksonUtils.toJson(instance));
