public ServiceInfo processServiceJson(String json) {
    // è§£æå‡ºServiceInfoä¿¡æ¯
    ServiceInfo serviceInfo = JacksonUtils.toObj(json, ServiceInfo.class);
    String serviceKey = serviceInfo.getKey();
    if (serviceKey == null) {
        return null;
    }
    // æŸ¥è¯¢ç¼“å­˜ä¸­çš„ ServiceInfo
    ServiceInfo oldService = serviceInfoMap.get(serviceKey);

    // å¦‚æœç¼“å­˜å­˜åœ¨ï¼Œåˆ™éœ€è¦æ ¡éªŒå“ªäº›æ•°æ®è¦æ›´æ–°
    boolean changed = false;
    if (oldService != null) {
		// æ‹‰å–çš„æ•°æ®æ˜¯å¦å·²ç»è¿‡æœŸ
        if (oldService.getLastRefTime() > serviceInfo.getLastRefTime()) {
            NAMING_LOGGER.warn("out of date data received, old-t: " + oldService.getLastRefTime() + ", new-t: "
                               + serviceInfo.getLastRefTime());
        }
        // æ”¾å…¥ç¼“å­˜
        serviceInfoMap.put(serviceInfo.getKey(), serviceInfo);
		
        // ä¸­é—´æ˜¯ç¼“å­˜ä¸æ–°æ•°æ®çš„å¯¹æ¯”ï¼Œå¾—åˆ°newHostsï¼šæ–°å¢çš„å®ä¾‹ï¼›remvHostsï¼šå¾…ç§»é™¤çš„å®ä¾‹;
        // modHostsï¼šéœ€è¦ä¿®æ”¹çš„å®ä¾‹
        if (newHosts.size() > 0 || remvHosts.size() > 0 || modHosts.size() > 0) {
            // å‘å¸ƒå®ä¾‹å˜æ›´çš„äº‹ä»¶
            NotifyCenter.publishEvent(new InstancesChangeEvent(
                serviceInfo.getName(), serviceInfo.getGroupName(),
                serviceInfo.getClusters(), serviceInfo.getHosts()));
            DiskCache.write(serviceInfo, cacheDir);
        }

    } else {
        // æœ¬åœ°ç¼“å­˜ä¸å­˜åœ¨
        changed = true;
        // æ”¾å…¥ç¼“å­˜
        serviceInfoMap.put(serviceInfo.getKey(), serviceInfo);
        // ç›´æ¥å‘å¸ƒå®ä¾‹å˜æ›´çš„äº‹ä»¶
        NotifyCenter.publishEvent(new InstancesChangeEvent(
            serviceInfo.getName(), serviceInfo.getGroupName(),
            serviceInfo.getClusters(), serviceInfo.getHosts()));
        serviceInfo.setJsonFromServer(json);
        DiskCache.write(serviceInfo, cacheDir);
    }
	// ã€‚ã€‚ã€‚
    return serviceInfo;
}
