    // rule.getLimitApp()å¾—åˆ°çš„å°±æ˜¯ ç™½åå• æˆ– é»‘åå• çš„å­—ç¬¦ä¸²ï¼Œè¿™é‡Œå…ˆç”¨ indexOfæ–¹æ³•åˆ¤æ–­
    int pos = rule.getLimitApp().indexOf(requester);
    boolean contain = pos > -1;

    if (contain) {
        // å¦‚æœåŒ…å« originï¼Œè¿˜è¦è¿›ä¸€æ­¥åšç²¾ç¡®åˆ¤æ–­ï¼ŒæŠŠåå•åˆ—è¡¨ä»¥","åˆ†å‰²ï¼Œé€ä¸ªåˆ¤æ–­
        boolean exactlyMatch = false;
        String[] appArray = rule.getLimitApp().split(",");
        for (String app : appArray) {
            if (requester.equals(app)) {
                exactlyMatch = true;
                break;
            }
        }
        contain = exactlyMatch;
    }
	// å¦‚æœæ˜¯é»‘åå•ï¼Œå¹¶ä¸”åŒ…å«originï¼Œåˆ™è¿”å›false
    int strategy = rule.getStrategy();
    if (strategy == RuleConstant.AUTHORITY_BLACK && contain) {
        return false;
    }
	// å¦‚æœæ˜¯ç™½åå•ï¼Œå¹¶ä¸”ä¸åŒ…å«originï¼Œåˆ™è¿”å›false
    if (strategy == RuleConstant.AUTHORITY_WHITE && !contain) {
        return false;
    }
	// å…¶å®ƒæƒ…å†µè¿”å›true
    return true;
}
