    // Ensure the checking switch is on.
    if (!checkSystemStatus.get()) {
        return;
    }

    // åªé’ˆå¯¹å…¥å£èµ„æºåšæ ¡éªŒï¼Œå…¶å®ƒç›´æ¥è¿”å›
    if (resourceWrapper.getEntryType() != EntryType.IN) {
