    }

    // å…¨å±€ ç³»ç»Ÿè´Ÿè½½ æ ¡éªŒ
    if (highestSystemLoadIsSet && getCurrentSystemAvgLoad() > highestSystemLoad) {
        if (!checkBbr(currentThread)) {
