    while (true) {
        WindowWrap<T> old = array.get(idx);
        if (old == null) {
            // åˆ›å»ºæ–° window
            WindowWrap<T> window = new WindowWrap<T>(windowLengthInMs, windowStart, newEmptyBucket(timeMillis));
            // åŸºäºCASå†™å…¥æ•°ç»„ï¼Œé¿å…çº¿ç¨‹å®‰å…¨é—®é¢˜
            if (array.compareAndSet(idx, null, window)) {
