@Override
public boolean canPass(Node node, int acquireCount, boolean prioritized) {
    // Pass when acquire count is less or equal than 0.
    if (acquireCount <= 0) {
        return true;
    }
    // é˜ˆå€¼å°äºç­‰äº 0 ï¼Œé˜»æ­¢è¯·æ±‚
    if (count <= 0) {
